import React, { Component } from 'react';
import './App.css';
import tracker from '../src/api/tracker';
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import { Link } from 'react-router-dom';
import { BiCommentEdit, BiShoppingBag, BiMenu, BiPencil } from "react-icons/bi";

import { Button, Container, Form, Input, Row,Col } from 'reactstrap';
import Table from 'react-bootstrap/Table';
import { SidebarContent } from 'react-pro-sidebar';

class pm_edit_product extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            brand:"",
            category:"",
            description: "",
            price: 0,
            rate: 0,
            image: [],
            countInStock:0,
            size: [],
            gender: "",
        }
    }
    componentDidMount = async () => {
        try {
            const id = this.props.match.params.id;
            const response = await tracker.get("product/" + id);
            if (response.data) {
                this.setState({
                    name: response.data.name,
                    brand: response.data.brand,
                    category: response.data.category,
                    description: response.data.description,
                    price: response.data.price,
                    rate: response.data.rate,
                    image: response.data.image,
                    countInStock: response.data.countInStock,
                    size: response.data.size,
                    gender: response.data.gender,
                });
            }

        } catch (e) { console.log(e) }
    }
    handleSubmit = async () => {

    }
    render() {
        const id = this.state.id;
        const name = this.state.name;
        const brand = this.state.brand;
        const category = this.state.category;
        const description = this.state.description;
        const price = this.state.price;
        const count = this.state.countInStock;
        const size = this.state.size;
        const gender = this.state.gender;
        return (
            < div className="form" >

                < Container className="form-container" >

                    < Row className="form" >
                        < Form >
                            < div className="login-check" >Edit Product</div ><br />
                            <Row><Col>
                                < Input className="mb-4" type="text" onChange={this.state.id} placeholder={id} />
                                < Input className="mb-4" type="text" onChange={this.state.name} placeholder={name} />
                                < Input className="mb-4" type="text" onChange={this.state.brand} placeholder={brand} /> </Col><Col>
                                    < Input className="mb-4" type="text" onChange={this.state.category} placeholder={category} />
                                    < Input className="mb-4" type="text" onChange={this.state.description} placeholder={description} />

                                    < Input className="mb-4" type="number" onChange={this.state.price} placeholder={price} /> </Col><Col>
                                    < Input className="mb-4" type="number" onChange={this.state.countInStock} placeholder={count} />

                                    < Input className="mb-4" type="number" onChange={this.state.size} placeholder={size} />

                                    <div className='mb-4'>
                                        <select value={this.state.gender} onChange={this.state.gender} className='Selectopt'>
                                            <option>Select gender</option>
                                            <option value="male">Male</option>
                                            <option value="female">Female</option>
                                        </select>
                                    </div>
                                </Col>  </Row>
                            < Button className="b-login" onClick={this.handleSubmit} color="secondary" block >Update</Button >



                        </Form >

                    </Row >

                </Container >

            </div >

            );
    }
}
export default pm_edit_product;
