import React, { Component } from "react";

import "./SelectOpt";
import "./Checkbox";
import { Button, Col, Container, Form, Input, Modal, Row } from "reactstrap";
import tracker from "../src/api/tracker";

class menAllCategories extends Component {
  constructor() {
    super();
    this.state = {
      products: [],

    };
  }

  componentDidMount = async () => {
    try {
      const response = await tracker.get("product/gender/male");
      const arrayproduct = response.data;
      this.setState({ products: arrayproduct });
      console.log("state", this.state.products);
    } catch (e) {}
  };
  handleProducts = () => {
    console.log("handleproduct");
    var product = [];
    if (this.state.products.length !== 0) {
      var productitem;
      for (var i = 0; i < this.state.products.length; i++) {
        productitem = (
            <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh", justifyContent: "space-around" }}>
            {/* <img style={{ width: "25vh", height: "25vh" }} src={this.state.images} alt="High Heels" /> */}
            <h5>{this.state.products[i].name}</h5>
            <p class="price">${this.state.products[i].price[0]}</p>
            <p>{this.state.products[i].definition}</p>
            <p>
              <button>Add to Cart</button>
            </p>
          </div>
        );
        product.push(productitem);
      }
      for (var i = 0; i < this.state.products.length; i++) {
        productitem = (
            <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh" , justifyContent: "space-around" }}>
            <img style={{ width: "25vh", height: "25vh" }} src={this.state.images} alt="High Heels" />
            <h5>{this.state.products[i].name}</h5>
            <p class="price">${this.state.products[i].price[0]}</p>
            <p>{this.state.products[i].definition}</p>
            <p>
              <button>Add to Cart</button>
            </p>
          </div>
        );
        product.push(productitem);
      }
      for (var i = 0; i < this.state.products.length; i++) {
        productitem = (
            <div className="card" style={{ width: "25vh", height: "50vh", margin: "2vh", justifyContent: "space-around" }}>
            <img style={{ width: "25vh", height: "25vh" }} src={this.state.images} alt="High Heels" />
            <h5>{this.state.products[i].name}</h5>
            <p class="price">${this.state.products[i].price[0]}</p>
            <p>{this.state.products[i].definition}</p>
            <p>
              <button>Add to Cart</button>
            </p>
          </div>
        );
        product.push(productitem);
      }

      console.log(product);
      return product;
    }
  };

  render() {
    return (
      <>
        <Container style={{ width: "auto", textAlign: "-webkit-center" }}>
          <div className="card" style={{ width: "auto", marginRight: "1vh", marginLeft: "1vh", maxWidth: "initial" }}>
            <h1 className="mt-5">Men All Categories</h1>
            <Row style={{ margin: "5vh", justifyContent: "space-around" }}>{this.handleProducts()}</Row>
          </div>
        </Container>
      </>
    );
  }
}
export default menAllCategories;
