import React, { Component } from 'react';

import './App.css';
import { Button, Container, Form, Input, Row, Col } from 'reactstrap';
import tracker from '../src/api/tracker';
import { ProSidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import { Link } from 'react-router-dom';
import { BiCommentEdit, BiShoppingBag, BiMenu } from "react-icons/bi";
import Select from 'react-select';
// import MultiSelect from "react-multi-select-component";
// import { MultiSelect } from '@progress/kendo-react-dropdowns';
import { SidebarContent } from 'react-pro-sidebar';

const options = [
    { label: "36", value: "36" },
    { label: "37", value: "37" },
    { label: "38", value: "38" },
    { label: "39", value: "39" },
    { label: "40", value: "40" },
    { label: "41", value: "41" },
    { label: "42", value: "42" },
    { label: "43", value: "43" },
    { label: "44", value: "44" },
    { label: "45", value: "45" },
];

class pm_add_product extends Component {
    constructor() {
        super();
        this.state = {
            productManager: "",
            id: "",
            name: "",
            brand: "",
            category: "",
            description: "",
            price: 0,
            value: [],
            //seller: { type: mongoose.Schema.Types.ObjectID, ref: 'user' },
            countInStock: 0,
            size: [],
            gender: "",
            selectedOption: null,
        }
    }
    handleSubmit = async () => {
        try {
            var pm = localStorage.getItem("isProductManager");
            if (pm === true) {
                var userid = localStorage.getItem("userId");

                const response = await tracker.post("/product", {
                    productManager: userid, //product manager id al�nacak

                    name: this.state.name,
                    brand: this.state.brand,
                    category: this.state.category,
                    description: this.state.description,
                    price: this.state.price,
                    rate: this.state.rate,

                    countInStock: this.state.countInStock,
                    size: this.state.size, //array olmal�
                    gender: this.state.gender,


                })
            }


        } catch (e) { console.log(e); }



    }
    handleSize = (event) => {
        //this.setState({value: event.option});
        this.setState({ size: Array.from(event.target.selectedOptions, (item) => item.value) });
        console.log(this.state.size)
    }



        handleChange = selectedOption => {
            this.setState(
              { selectedOption },
              () => console.log(`Option selected:`, this.state.selectedOption)
            );
          };

    render() {
        return (
            <>
                <div style={{ marginLeft: "-125px", paddingBottom: "2rem" }}>
                    <ProSidebar >

                        <SidebarContent>
                            <Menu iconShape="square" icon={<BiMenu />} >
                                <MenuItem icon={<BiCommentEdit />}>Comments<Link to="/product_manager_comments" /></MenuItem>
                                <SubMenu title="Edit Products" icon={<BiShoppingBag />}>
                                    <MenuItem >Add Product<Link to="/pm_add_product" /></MenuItem>
                                    <MenuItem>Delete Product<Link to='/pm_delete_product' /></MenuItem>

                                </SubMenu>
                            </Menu>
                        </SidebarContent>


                    </ProSidebar>
                </div>
                < div className="form" >

                    < Container className="form-container" >

                        < Row className="form" >
                            < Form >
                                < div className="login-check" >Add New Product</div ><br />
                                <Row><Col>
                                    {/* < Input className="mb-4" type="text" onChange={this.state.id} placeholder="Enter product id" /> */}
                                    < Input className="mb-4" type="text" onChange={this.state.name} placeholder="Enter product name" />
                                    < Input className="mb-4" type="text" onChange={this.state.brand} placeholder="Enter brand" /> </Col><Col>
                                        < Input className="mb-4" type="text" onChange={this.state.category} placeholder="Enter category" />
                                        < Input className="mb-4" type="text" onChange={this.state.description} placeholder="Enter description" />

                                        < Input className="mb-4" type="tel" onChange={this.state.price} placeholder="Enter price" /> </Col><Col>
                                        < Input className="mb-4" type="tel" onChange={this.state.countInStock} placeholder="Enter count in stock" />
                                        <div className='mb-4'>
                                            {/* <MultiSelect
                                                options={options}
                                                value={selected}
                                                onChange={setSelected}
                                                labelledBy={"Select"}
                                            /> */}
                                            {/* <MultiSelect
                    data={sizes}
                    value={this.state.value}
                    onChange={this.handleChange}

                /> */}   <Select
                                                value={selectedOption}
                                                onChange={this.handleChange}
                                                options={options}
                                            />
                                        </div>
                                        <div className='mb-4'>
                                            <select value={this.state.Gender} onChange={this.state.gender} className='Selectopt'>
                                                <option>Select gender</option>
                                                <option value="male">Male</option>
                                                <option value="female">Female</option>
                                            </select>
                                        </div>
                                    </Col>  </Row>
                                < Button className="b-login" onClick={this.handleSubmit} color="secondary" block >Add</Button >



                            </Form >

                        </Row >

                    </Container >

                </div >
            </>

        );
    }
}
export default pm_add_product;
